import React, { useState, useEffect } from 'react';
import { Cropper } from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import './CropResize.css';

function CropResize() {
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [cropper, setCropper] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [aspectRatio, setAspectRatio] = useState(null);

  const aspectRatios = [
    { label: 'Free', value: null },
    { label: '1:1', value: 1 },
    { label: '4:3', value: 4/3 },
    { label: '16:9', value: 16/9 },
    { label: '2:3', value: 2/3 }
  ];

  useEffect(() => {
    const storedImages = sessionStorage.getItem('uploadedImages');
    if (storedImages) {
      const parsedImages = JSON.parse(storedImages);
      setImages(parsedImages);
      if (parsedImages.length > 0) {
        setSelectedImage(parsedImages[0]);
      }
    }
  }, []);

  useEffect(() => {
    return () => {
      // Clean up old URLs when component unmounts
      images.forEach(image => {
        if (image.url.startsWith('blob:')) {
          URL.revokeObjectURL(image.url);
        }
      });
    };
  }, [images]);

  const handleCrop = () => {
    if (cropper) {
      const croppedCanvas = cropper.getCroppedCanvas({
        width: 1080, 
        height: 1080,
        imageSmoothingEnabled: true,
        imageSmoothingQuality: 'high',
      });

      if (croppedCanvas) {
        // Convert canvas to base64 string
        const base64Data = croppedCanvas.toDataURL('image/jpeg', 0.95);

        // Create new image name with timestamp
        const timestamp = new Date().getTime();
        const newImageName = `${selectedImage.name.split('.')[0]}_cropped_${timestamp}.jpg`;

        // Create new image object
        const newImage = {
          url: base64Data,
          name: newImageName
        };

        // Add the cropped image to the array
        const updatedImages = [...images, newImage];

        // Update state
        setImages(updatedImages);
        setSelectedImage(newImage);

        // Update sessionStorage with the new images array
        try {
          sessionStorage.setItem('uploadedImages', JSON.stringify(updatedImages));
          console.log('Successfully saved cropped image to sessionStorage');
        } catch (error) {
          console.error('Error saving to sessionStorage:', error);
        }

        // Reset cropping controls
        setIsEditing(false);
        setAspectRatio(null);

        // Force cropper instance reset
        if (cropper) {
          cropper.reset();
          cropper.clear();
        }
      }
    }
  };

  const resetCropperState = () => {
    setIsEditing(false);
    setAspectRatio(null);
    if (cropper) {
      cropper.reset();
      cropper.clear();
    }
  };

  useEffect(() => {
    resetCropperState();
  }, [selectedImage]);

  const handleRotate = (degrees) => {
    if (cropper) {
      cropper.rotate(degrees);
    }
  };

  const handleAspectRatioChange = (ratio) => {
    setAspectRatio(ratio);
    if (cropper) {
      // Clear existing crop area
      cropper.clear();
      // Set new aspect ratio
      cropper.setAspectRatio(ratio);
      // Reset crop box to full image with new aspect ratio
      cropper.reset();
      // Center the crop box
      const containerData = cropper.getContainerData();
      const cropBoxData = cropper.getCropBoxData();
      const centerX = (containerData.width - cropBoxData.width) / 2;
      const centerY = (containerData.height - cropBoxData.height) / 2;
      cropper.setCropBoxData({
        left: centerX,
        top: centerY
      });
    }
  };

  const handleImageSelect = (image) => {
    setSelectedImage(image);
    resetCropperState();
  };

  if (images.length === 0) {
    return (
      <div className="crop-resize-container">
        <div className="no-images">
          <h3>No Images Available</h3>
          <p>Please upload some images first</p>
        </div>
      </div>
    );
  }

  return (
    <div className="crop-resize-container">
      <div className="editor-main">
        {selectedImage && (
          <>
            <div className="image-workspace">
              {isEditing ? (
                <Cropper
                  src={selectedImage.url}
                  style={{ height: '100%', width: '100%' }}
                  aspectRatio={aspectRatio}
                  guides={true}
                  viewMode={1}
                  minCropBoxHeight={10}
                  minCropBoxWidth={10}
                  background={false}
                  responsive={true}
                  autoCropArea={1}
                  checkOrientation={false}
                  onInitialized={(instance) => setCropper(instance)}
                />
              ) : (
                <img src={selectedImage.url} alt={selectedImage.name} />
              )}
            </div>
            <div className="editor-controls">
              <div className="controls-header">
                <h3>Crop & Resize Controls</h3>
                <button 
                  className={`edit-button ${isEditing ? 'active' : ''}`}
                  onClick={() => setIsEditing(!isEditing)}
                >
                  {isEditing ? 'Cancel' : 'Edit Image'}
                </button>
              </div>
              {isEditing && (
                <>
                  <div className="aspect-ratio-controls">
                    {aspectRatios.map((ratio, index) => (
                      <button
                        key={index}
                        className={`aspect-ratio-button ${aspectRatio === ratio.value ? 'active' : ''}`}
                        onClick={() => handleAspectRatioChange(ratio.value)}
                      >
                        {ratio.label}
                      </button>
                    ))}
                  </div>
                  <div className="control-buttons">
                    <button onClick={() => handleRotate(-90)}>
                      Rotate Left
                    </button>
                    <button onClick={() => handleRotate(90)}>
                      Rotate Right
                    </button>
                    <button className="primary-button" onClick={handleCrop}>
                      Apply Crop
                    </button>
                  </div>
                </>
              )}
            </div>
          </>
        )}
      </div>
      <div className="image-sidebar">
        {images.map((image, index) => (
          <div 
            key={image.url}
            className={`thumbnail ${selectedImage === image ? 'selected' : ''}`}
            onClick={() => handleImageSelect(image)}
          >
            <img src={image.url} alt={image.name} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default CropResize;
