import React, { useState, useRef } from 'react';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import './CropResize.css';

function CropResize({ onNext, onBack }) {
  const [images, setImages] = useState(() => {
    const savedImages = sessionStorage.getItem('uploadedImages');
    return savedImages ? JSON.parse(savedImages) : [];
  });

  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const cropperRef = useRef(null);

  const handleCrop = () => {
    const cropper = cropperRef.current?.cropper;
    if (cropper) {
      const croppedCanvas = cropper.getCroppedCanvas();
      const croppedImage = croppedCanvas.toDataURL();

      const updatedImages = [...images];
      updatedImages[currentImageIndex] = {
        ...updatedImages[currentImageIndex],
        url: croppedImage
      };

      setImages(updatedImages);
      sessionStorage.setItem('uploadedImages', JSON.stringify(updatedImages));
    }
  };

  const handleNext = () => {
    if (currentImageIndex < images.length - 1) {
      setCurrentImageIndex(currentImageIndex + 1);
    } else {
      onNext('Generate');
    }
  };

  const handlePrevious = () => {
    if (currentImageIndex > 0) {
      setCurrentImageIndex(currentImageIndex - 1);
    }
  };

  return (
    <div className="crop-resize-container">
      <div className="cropper-container">
        {images.length > 0 && (
          <Cropper
            ref={cropperRef}
            src={images[currentImageIndex].url}
            style={{ height: 400, width: '100%' }}
            aspectRatio={1}
            guides={true}
            viewMode={1}
            dragMode="move"
            scalable={true}
            zoomable={true}
            autoCropArea={1}
            background={false}
          />
        )}
      </div>

      <div className="controls">
        <button 
          onClick={onBack}
          className="control-button"
        >
          Back to Upload
        </button>

        <button 
          onClick={handleCrop}
          className="control-button"
        >
          Crop Image
        </button>

        <button 
          onClick={handlePrevious}
          disabled={currentImageIndex === 0}
          className="control-button"
        >
          Previous Image
        </button>

        <button 
          onClick={handleNext}
          className="control-button"
        >
          {currentImageIndex === images.length - 1 ? 'Next Step' : 'Next Image'}
        </button>
      </div>

      <div className="image-counter">
        Image {currentImageIndex + 1} of {images.length}
      </div>
    </div>
  );
}

export default CropResize;
