import React, { useState } from 'react';
import './Upload.css';

function Upload({ onNext }) {
  const [files, setFiles] = useState([]);
  const [previews, setPreviews] = useState([]);

  const handleFileSelect = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles(selectedFiles);
    createPreviews(selectedFiles);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const droppedFiles = Array.from(event.dataTransfer.files);
    setFiles(droppedFiles);
    createPreviews(droppedFiles);
  };

  const createPreviews = (selectedFiles) => {
    const newPreviews = selectedFiles.map(file => ({
      url: URL.createObjectURL(file),
      name: file.name,
      file: file
    }));
    setPreviews(newPreviews);
  };

  const handleDelete = (indexToDelete) => {
    // Revoke the URL to prevent memory leaks
    URL.revokeObjectURL(previews[indexToDelete].url);

    // Remove the file from both arrays
    const newFiles = files.filter((_, index) => index !== indexToDelete);
    const newPreviews = previews.filter((_, index) => index !== indexToDelete);

    setFiles(newFiles);
    setPreviews(newPreviews);
  };

  const handleNextClick = () => {
    // Store images in sessionStorage
    const imageData = previews.map(preview => ({
      url: preview.url,
      name: preview.name
    }));
    sessionStorage.setItem('uploadedImages', JSON.stringify(imageData));
    onNext('Crop & Resize');
  };

  return (
    <div className="upload-container">
      <div 
        className="upload-area"
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleFileSelect}
          className="file-input"
          id="file-input"
        />
        <label htmlFor="file-input" className="upload-label">
          <div className="upload-label">
            <div className="upload-icon">
              <span role="img" aria-label="Upload">ðŸ“¤</span>
            </div>
            <p>Drag and drop your photos here or click to browse</p>
            <span className="upload-hint">Supports multiple images</span>
          </div>
        </label>
      </div>

      {previews.length > 0 && (
        <div className="preview-container">
          <h3>Selected Photos ({previews.length})</h3>
          <div className="preview-grid">
            {previews.map((preview, index) => (
              <div key={index} className="preview-item">
                <button 
                  className="delete-button" 
                  onClick={() => handleDelete(index)}
                  aria-label="Delete photo"
                >
                  Ã—
                </button>
                <img src={preview.url} alt={preview.name} />
                <p>{preview.name}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {previews.length > 0 && (
        <div className="upload-actions">
          <button 
            className="next-button"
            onClick={handleNextClick}
          >
            Next: Crop & Resize
          </button>
        </div>
      )}
    </div>
  );
}

export default Upload;
