import React, { useState, useRef } from 'react';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import './CropResize.css';

function CropResize({ onNext, onBack }) {
  const [images, setImages] = useState(() => {
    const savedImages = sessionStorage.getItem('uploadedImages');
    return savedImages ? JSON.parse(savedImages) : [];
  });

  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isCropMode, setIsCropMode] = useState(false);
  const [aspectRatio, setAspectRatio] = useState(1);
  const cropperRef = useRef(null);

  const aspectRatioOptions = [
    { label: '1:1', value: 1 },
    { label: '4:3', value: 4/3 },
    { label: '16:9', value: 16/9 },
    { label: '3:4', value: 3/4 },
    { label: 'Free', value: NaN }
  ];

  const handleStartCrop = () => {
    setIsCropMode(true);
  };

  const handleCancelCrop = () => {
    setIsCropMode(false);
  };

  const handleSaveCrop = () => {
    const cropper = cropperRef.current?.cropper;
    if (cropper) {
      const croppedCanvas = cropper.getCroppedCanvas();
      const croppedImage = croppedCanvas.toDataURL();

      const newImage = {
        url: croppedImage,
        name: `${images[currentImageIndex].name}_cropped`,
        originalIndex: currentImageIndex
      };

      const updatedImages = [...images, newImage];
      setImages(updatedImages);
      setCurrentImageIndex(updatedImages.length - 1);
      sessionStorage.setItem('uploadedImages', JSON.stringify(updatedImages));
      setIsCropMode(false);
    }
  };

  const handleThumbnailClick = (index) => {
    if (!isCropMode) {
      setCurrentImageIndex(index);
    }
  };

  const handleAspectRatioChange = (ratio) => {
    setAspectRatio(ratio);
  };

  return (
    <div className="crop-resize-wrapper">
      <div className="crop-resize-container">
        <section className="editor-section">
          <h2 className="section-title">Image Editor</h2>
          <div className="cropper-container">
            {images.length > 0 && (
              <Cropper
                ref={cropperRef}
                src={images[currentImageIndex].url}
                style={{ height: 500, width: '100%' }}
                aspectRatio={aspectRatio}
                guides={isCropMode}
                viewMode={2}
                dragMode={isCropMode ? "crop" : "move"}
                cropBoxMovable={isCropMode}
                cropBoxResizable={isCropMode}
                autoCropArea={1}
                background={true}
                responsive={true}
                zoomable={!isCropMode}
              />
            )}
          </div>

          <div className="controls">
            {!isCropMode ? (
              <>
                <button 
                  onClick={onBack} 
                  className="control-button secondary"
                  disabled={isCropMode}
                >
                  ← Back
                </button>

                <button 
                  onClick={handleStartCrop} 
                  className="control-button primary"
                >
                  Crop this photo
                </button>

                <button 
                  onClick={onNext} 
                  className="control-button primary"
                  disabled={isCropMode}
                >
                  Continue →
                </button>
              </>
            ) : (
              <>
                <div className="aspect-ratio-controls">
                  {aspectRatioOptions.map((option) => (
                    <button
                      key={option.label}
                      onClick={() => handleAspectRatioChange(option.value)}
                      className={`aspect-ratio-button ${aspectRatio === option.value ? 'active' : ''}`}
                    >
                      {option.label}
                    </button>
                  ))}
                </div>
                <div className="crop-actions">
                  <button 
                    onClick={handleCancelCrop} 
                    className="control-button secondary"
                  >
                    Cancel
                  </button>
                  <button 
                    onClick={handleSaveCrop} 
                    className="control-button primary"
                  >
                    Save Crop
                  </button>
                </div>
              </>
            )}
          </div>
        </section>

        <section className={`thumbnails-section ${isCropMode ? 'disabled' : ''}`}>
          <h2 className="section-title">Image Gallery</h2>
          <div className="thumbnails-grid">
            {images.map((image, index) => (
              <div 
                key={index}
                className={`thumbnail-card ${currentImageIndex === index ? 'active' : ''}`}
                onClick={() => handleThumbnailClick(index)}
              >
                <div className="thumbnail-image">
                  <img src={image.url} alt={`Version ${index + 1}`} />
                </div>
                <div className="thumbnail-info">
                  <span className="thumbnail-name">{image.name}</span>
                  {image.originalIndex !== undefined && (
                    <span className="thumbnail-badge">Cropped</span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
}

export default CropResize;
